const { join } = require('path');

exports.deploy = (_model, _db, options) => {
  // locate deployer relative to already located sap/cds -> local cds takes precedence
  const cdsHome = require('@sap/cds').home;
  const deployer = require(join(cdsHome, 'bin/deploy/to-hana'));

  const bind = require('../bind');
  return deployer.deploy(_model, _db, options, options.bind ? bind : null);
}
