const cds = require ('@sap/cds')

// cds.compile.to ...
'xsuaa' in cds.compile.to || Object.defineProperties (cds.compile.to,{
  'xsuaa':   {enumerable:true, get:()=> require('./to-xsuaa')},
  'openapi': {enumerable:true, get:()=> require('./openapi')},
  'edmx-v2': {enumerable:true, value: (csn,o) => cds.compile.to.edmx(csn,{...o,flavor:'v2'}) },
  'edmx-v4': {enumerable:true, value: (csn,o) => cds.compile.to.edmx(csn,{...o,flavor:'v4'}) },
  'edmx-w4': {enumerable:true, value: (csn,o) => cds.compile.to.edmx(csn,{...o,flavor:'w4'}) },
  'edmx-x4': {enumerable:true, value: (csn,o) => cds.compile.to.edmx(csn,{...o,flavor:'x4'}) },
})


module.exports = cds
